import React, { useState } from 'react';
import './style.css';

export default function App() {

  return (
    <div>
    <div
      style={{
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'space-between',
        maxWidth: '100%'
      }}
    >
      <h1>Matchs the pairs ðŸ¤”</h1>
      <p>Deploy in progress... </p>  
    </div>
      <div 
        style={{
          borderStyle: 'solid', 
          borderWidth: '1px',  
          borderColor: '#000'
      }}>      
      </div>
      <div
        style={{
          display: 'flex',
          justifyContent: 'space-evenly',
        }}
      >
        <p
          style={{
            width: '300px',
            height: '60px',
            backgroundColor: 'grey',
            borderRadius: '5px',
          }}
        >Pairs matched
        <br />
        1/8</p>
        <p
          style={{
            width: '300px',
            height: '60px',
            backgroundColor: 'grey',
            borderRadius: '5px',
          }}
        >Total moves
        <br />
        2</p>
      </div>
      <div 
        style={{
          display: 'grid',
          justifyContent: 'space-around',
          gridTemplateColumns: '12em 12em 12em 12em', 
          gridTemplateRows: 'auto auto auto auto',
          gap: '30px',
          border: '1px solid black'
        }}
      >
        <div
          style={{
            border: '5px solid grey',
            backgroundColor: 'grey',
            width: '300px',
            display: 'flex',
            justifyContent: 'center'
          }}
        >
        <img
          src='https://fr.web.img6.acsta.net/pictures/20/12/28/10/24/5603983.jpg'
          width={'200px'}
          height={'280px'}
        ></img>
        </div>

        <div
          style={{
            border: '5px solid grey',
            backgroundColor: 'grey',
            width: '300px',
            display: 'flex',
            justifyContent: 'center'
          }}
        >
        <img
          src='https://i.ibb.co/TtZdWMR/jujutsu-kaisen.webphttps://ibb.co/QNc1Vsc.webp'
          width={'200px'}
          height={'280px'}
        ></img>
        </div>

        <div
          style={{
            border: '5px solid grey',
            backgroundColor: 'grey',
            width: '300px',
            display: 'flex',
            justifyContent: 'center'
          }}
        >
        <img
          src='https://i.ibb.co/4PSLvzv/naruto.jpg'
          width={'200px'}
          height={'280px'}
        ></img>
        </div>


        <div
          style={{
            border: '5px solid grey',
            backgroundColor: 'grey',
            width: '300px',
            display: 'flex',
            justifyContent: 'center'
          }}
        >
        <img
          src='https://i.ibb.co/cDhJ4qv/kuroko-no-basket.webp'
          width={'200px'}
          height={'280px'}
        ></img>
        </div>


      <img
        src='https://i.ibb.co/HqJL3Qv/haikyuu.jpg'
        width={'200px'}
        height={'280px'}
      ></img>

      <img
        src='https://i.ibb.co/PtSSp1M/black-clover.jpg'
        width={'200px'}
        height={'280px'}
      ></img>

      <img
        src='https://i.ibb.co/vjjgWj5/goblin-slaye.jpg'
        width={'200px'}
        height={'280px'}
      ></img>

      <img
        src='https://i.ibb.co/yyRbCLR/code-geass.webp'
        width={'200px'}
        height={'280px'}
      ></img>

      </div>
      <button>
        Reset Game
      </button>
      <button>
        End Game
      </button>
    </div>
  )

}
