import React, { useState } from 'react';
import './style.css';

export default function App() {

  return (
    <div>
    <div
      style={{
        display: 'flex',
        justifyContent: 'space-between'
      }}
    >
      <h1>Matchs the pairs ðŸ¤”</h1>
      <p>Deploy in progress... </p>  
    </div>
      <div 
        style={{
          borderStyle: 'solid', 
          borderWidth: '1px',  
          borderColor: '#000'
      }}>      
      </div>
      <div
        style={{
          display: 'flex',
          justifyContent: 'space-evenly',
        }}
      >
        <p
          style={{
            width: '300px',
            height: '60px',
            backgroundColor: 'grey',
            borderRadius: '5px',

          }}
        >Pairs matched
        <br />
        1/8</p>
        <p
          style={{
            width: '300px',
            height: '60px',
            backgroundColor: 'grey',
            borderRadius: '5px',
          }}
        >Total moves
        <br />
        2</p>
      </div>
      <div 
        style={{
          display: 'grid',
          justifyContent: 'space-around',
          gridTemplateColumns: '12em 12em 12em 12em', 
          gridTemplateRows: 'auto auto auto auto',
          gap: '30px'
        }}
      >
      <img
        src='https://i.ibb.co/0hp8fm0/moi-quand-je-me-re-incarne-en-slime.webp'
        width={'200px'}
        height={'280px'}
      ></img>
      <img
        src='https://i.ibb.co/TtZdWMR/jujutsu-kaisen.webphttps://ibb.co/QNc1Vsc.webp'
        width={'200px'}
        height={'280px'}
      ></img>
      <img
        src='https://i.ibb.co/4PSLvzv/naruto.jpg'
        width={'200px'}
        height={'280px'}
      ></img>
      <img
        src='https://i.ibb.co/cDhJ4qv/kuroko-no-basket.webp'
        width={'200px'}
        height={'280px'}
      ></img>
      <img
        src='https://i.ibb.co/HqJL3Qv/haikyuu.jpg'
        width={'200px'}
        height={'280px'}
      ></img>
      <img
        src='https://i.ibb.co/PtSSp1M/black-clover.jpg'
        width={'200px'}
        height={'280px'}
      ></img>
      <img
        src='https://i.ibb.co/vjjgWj5/goblin-slaye.jpg'
        width={'200px'}
        height={'280px'}
      ></img>
      <img
        src='https://i.ibb.co/yyRbCLR/code-geass.webp'
        width={'200px'}
        height={'280px'}
      ></img>
      </div>
      <button>
        Reset Game
      </button>
      <button>
        End Game
      </button>
    </div>
  )

}
